import React, { Component } from "react";

export default class Pagination extends Component {
  constructor(props) {
    super(props)

    this.state = {
      data: null,
      total: null,
      per_page: null,
      current_page: null
    }
  }

  makeHttpRequestWithPage = async pageNumber => {
    let response = await fetch(`http://elearning0706.cybersoft.edu.vn/api/QuanLyKhoaHoc/LayDanhSachKhoaHoc_PhanTrang?page=${pageNumber}&pageSize=10&MaNhom=GP01`, {
      method: 'GET',
      
    });

    const data = await response.json();

    this.setState({
      users: data.data,
      total: data.total,
      per_page: data.per_page,
      current_page: data.page,
    });
  }

