import { FETCH_USER, ADD_USER } from "../Action/type"

const initialState = []

const user = (state = initialState, { type, payload }) => {
    switch (type) {

        case FETCH_USER:
            return payload
        case ADD_USER: {
            let newArr = [...state]
            newArr = state.push(payload)
            state = newArr
            
        }
        default:
            return state
    }
}

export default user
