      url: `/api/QuanLyKhoaHoc/LayThongTinKhoaHoc?maKhoaHoc=111`,
      method: "GET"
    });
  }

  layDanhSachKhoaHoc() {
    return resConnector({
      url: "/api/QuanLyKhoaHoc/LayDanhSachKhoaHoc?MaNhom=GP01",
      method: "GET"
    });
  }

  fetchCourseCatalog() {
    return resConnector({
      url: "/api/QuanLyKhoaHoc/LayDanhMucKhoaHoc",
      method: "GET"
    });
  }


  fetchCourseDetail() {
    return resConnector({
      url: "/api/QuanLyKhoaHoc/LayDanhMucKhoaHoc",
      method: "GET"
    });
  }

