import React, { Component } from "react";
import UserService from "../../Services/userService";
import { DELETE_USER } from "../../Store/Action/type";
import { notify } from "../notify/Notify";
import { connect } from "react-redux";
import reduxAction from "../../Store/Action/action";
let userService = new UserService();
class UserItem extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user: {
                taiKhoan: "string",
                matKhau: "string",
                hoTen: "string",
                soDT: "string",
                maLoaiNguoiDung: "string",
                maNhom: "string",
                email: "string"
            }
        }
    }


    onChange = e => {
        this.setState(
            {
                user: {
                    ...this.state.course,
                    [e.target.name]: e.target.value
                }
            },
            console.log(this.state.course)
        );
    };





    static getDerivedStateFromProps(nextProps, prevState) {
        console.log("getDerivedStateFromProps");

        if (nextProps.editUser.taiKhoan !== prevState.user.taiKhoan) {
            return {
                ...prevState, user: nextProps.editUser
            }
        }
        return null
    }
    onDeleteUser = () => {
        let { taiKhoan } = this.props.user;

        userService
            .deleteUser(this.props.user.taiKhoan)
            .then(res => {
                console.log(res.data);

                this.props.dispatch(reduxAction(DELETE_USER, taiKhoan));
                notify("", "Xóa thành công");
            })
            .catch(err => {
                notify("", 'tài khoản này ko thể xóa')

            });
    };

    render() {
        let { user } = this.props;
        return (
            <tr>
                <td>
                    <div className="name__wrapper d-flex">
                        <img
                            alt=""
                            src="/img/avatar.png"
                            style={{ height: "60px", width: "60px" }}
                        />
                        <div className="course__name ml-3">
                            <h6 style={{ fontWeight: "bold" }}>{user.taiKhoan}</h6>
                            <span className="badge badge-info">development</span>
                        </div>
                    </div>
                </td>
                <td>
                    <h6>{user.hoTen}</h6>
                </td>
                <td>{user.soDt}</td>
                <td>{user.email}</td>
                <td>
                    <button
                        className="btn btn-info mr-2"
                        data-toggle="modal" data-target="#fix"
                    >
                        <i className="fa fa-pencil" aria-hidden="true" />
                    </button>





                    <div class="modal fade" id="fix" tabindex="-1" role="dialog" aria-labelledby="modelTitleId" aria-hidden="true">
                        <div class="modal-dialog" role="document">
                            <div class="modal-content">
                                <div class="modal-header">
                                    <h5 class="modal-title">Modal title</h5>
                                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div class="modal-body">
                                    Body
                                </div>
                                <div class="modal-footer">
                                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                                    <button type="button" class="btn btn-primary">Save</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <button className="btn btn-danger" onClick={this.onDeleteUser}>
                        <i className="fa fa-user-times" aria-hidden="true" />
                    </button>
                </td>
            </tr>
        );
    }
}
const mapStateToProps = (state) => ({
    editUser: state.editUser || {}
})

export default connect(mapStateToProps)(UserItem)
