import { resConnector } from "../Services/index";

const user = JSON.parse(localStorage.getItem("userLogin"))

class UserService {
  UserSignUp(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangKy",
      method: "POST",
      data: user
    });
  }

  UserLogin(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangNhap",
      method: "POST",
      data: user
    });
  }

  personalInfo(taiKhoan) {
    return resConnector({
      method: "POST",
      url: "/api/QuanLyNguoiDung/ThongTinTaiKhoan",
      data: { taiKhoan },
      headers: {
        Authorization: "Bearer  " + user.accessToken
      }
    });
  }

  fixPersonalUser(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/CapNhatThongTinNguoiDung",
      method: "PUT",
      data: user,
      headers: {
        Authorization: "Bearer  " + user.accessToken
      }
    });
  }

  fetchListUser() {
    return resConnector({
      url: "/api/QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=GP01",
      method: "GET",

      headers: {
        Authorization: "Bearer  " + user.accessToken
      }
    });
  }

  addUser(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/ThemNguoiDung",
      method: "POST",
      data: user,
      headers: {
        Authorization: "Bearer  " + user.accessToken
      }
    });
  }

  deleteUser(taiKhoan) {
    return resConnector({
      url: `/api/QuanLyNguoiDung/XoaNguoiDung?TaiKhoan=${taiKhoan}`,
