import React, { Component } from "react";
import Header from "../Component/Header/Header";
import Footer from "../Component/Footer/Footer";
import Viewing from "../Component/Viewing/Viewing";

import CourseService from "../Services/courseService";

import { FETCH_COURSE_DETAIL } from "../Store/Action/type";
import { connect } from "react-redux";

import CourseItem from "../Component/courseItem/CourseItem";
const courseService = new CourseService();
class CourseDetailPage extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
      showGoCart: false
    }
  }
  
  // componentDidMount() {
  //   // lấy tham số mã khóa học từ url
  //   const { courseid } = this.props.match.params;
  //   courseService
  //     .fetchCourseDetail(courseid)
  //     .then(res => {
  //       this.props.dispatch(reduxAction(FETCH_COURSE_DETAIL, res.data));
  //     })
  //     .catch(err => {
  //       console.log(err);
  //     });
  // }

  static getDerivedStateFromProps = (prevProps, nextState) => {
    let cart = JSON.parse(localStorage.getItem("cart")) ? JSON.parse(localStorage.getItem("cart")) : [];
    let index = cart.findIndex(item => {
      return item.maKhoaHoc === prevProps.course.maKhoaHoc;
    });
    if (index !== -1) {
      nextState.showGoCart = true
    }
    return null;



  }

  
  render() {
    let { courseDetail ,course } = this.props;
    console.log(this.props.courseDetail);

    return (
      <div>
        <Header />
        <CourseItem/>
        <Viewing />

        <Footer />
      </div>
    );
  }
}
const mapStateToProps = state => ({
  courseDetail: state.Course.courseDetail || {
    maKhoaHoc: "",
    tenKhoaHoc: "",
    hinhAnh: "",
    nguoiTao: {
      taiKhoan: "",
      hoTen: ""
    }
  },

  course : state.Course.course
});




export default connect(mapStateToProps)(CourseDetailPage);
