import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { ADD_TO_CART } from "../../Store/Action/type";
class Course extends Component {

  constructor(props) {
    super(props)

    this.state = {
      showGoCart: false
    }
  }

  onAdd = (course) => {
    this.props.addToCart(course)
    this.setState({
      showGoCart: true
    })

  }
  get

  
  render() {


    let { item } = this.props
    return (
      <div className="product__items">
        <div className="product__item">
          <Link style={{ textDecoration: "none" }} to={`/coursedetail/${item.maKhoaHoc}`}>
            <div className="item__img">
              <img src={item.hinhAnh} alt="" className="img-fluid" />
              <div className="img__overplay"></div>
            </div>
            <div className="item__info">
              <h5>{item.tenKhoaHoc}</h5>
              <h6>Jose Portilla</h6>
              <div className="raiting">
                <i className="fa fa-star"></i>
                <i className="fa fa-star"></i>
                <i className="fa fa-star"></i>
                <i className="fa fa-star"></i>
                <i className="fa fa-star"></i>
              </div>
              <div className="price">
                <span className="old__price">$199.99</span>
                <span className="new__price">
                  $19.99 <i className="fa fa-tag" aria-hidden="true" />

                </span>
              </div>
              

            </div>

            {
                this.state.showGoCart ?
                  <button className="btn btn-info">Go to Cart</button>
                  :
                  <button className="btn btn-danger" onClick={() => { this.onAdd(item) }}>Add to Cart</button>
              }

          </Link>


        </div>
      </div>
    );
  }
}



const mapDispatchToProps = dispatch => {
  return {
    addToCart: course => {
      dispatch({
        type: ADD_TO_CART,
        payload: course
      });
    }
  };
};

export default connect(null, mapDispatchToProps)(Course)
