import React, { Component } from "react";

export default class Pagination extends Component {
  constructor(props) {
    super(props)

    this.state = {
      currentPgae: 1,
      itemPgae: 16,
      startIndex: 0,
      endIndex: 3
    }
  }

  // funcion render ra cac page cua pagination 
  renderPageNumber = (pageNumber) => {
    let result = null;
    let active = false;
    let { startIndex, endIndex } = this.state
    if (pageNumbers !== undefined) {
        //cat ra array bang vi tri bat dau va ket thuc (so voi currentPage)
        result = pageNumbers.slice(startIndex, endIndex).map(number => {
            this.state.currentPage === number ? active = 'active' : active = '';
            if (number < pageNumbers.length) {
                return (
                    <li className={`pagi__item ${active}`}
                        id={number}
                        
                        onClick={this.handleClick}
                        key={number}>

                        {number}

                    </li>
                );
            }
            return "";
        })
    }
    return result;
  }


  render() {
    return (
      <div className="pagi">
        <ul className="pagi__wrapper">
          <li className="pagi__item disabled">
            <i className="fa fa-chevron-left" aria-hidden="true" />
          </li>
          <li className="pagi__item active" id="1">
            1
          </li>
          <li className="pagi__item " id="2">
            2
          </li>
          <li className="pagi__item " id="3">
            3
          </li>
          <li className="pagi__item disabled">...</li>
          <li className="pagi__item ">5</li>
          <li className="pagi__item ">
            <i className="fa fa-chevron-right" aria-hidden="true" />
          </li>
        </ul>
      </div>
    );
  }
}
