import React, { Component } from "react";
import CourseItem from "./CourseItem";
import CourseModal from "./CourseModal";
import { connect } from "react-redux";
import {
  SEARCH_COURSES,
  DELETE_COURSE,
  ADD_COURSE
} from "../../Store/Action/type";

class Course extends Component {
  render() {
    let { course, searchKeyword } = this.props;
    console.log(course);

    return (
      <div>
        <div className="title">
          <i className="fa fa-user"></i>
          My Course
        </div>
        <CourseModal
          handleSearch={this.props.handleSearch}
          addCourse={this.props.addCourse}
        />

        <table className="table">
          <thead>
            <tr>
              <th>
                Name <i className="fa fa-sort-alpha-desc" aria-hidden="true" />
              </th>
              <th>Created by</th>
              <th>Published at </th>
              <th>Displayed</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>{this.showCoursesItem(course, searchKeyword)}</tbody>
        </table>
      </div>
    );
  }

  showCoursesItem = (courses, searchKeyword) => {
    let result = [];
    // if (courses.length > 0) {
    //   if (searchKeyword.length > 0 && searchKeyword.length !== "") {
    //     courses = courses.filter(
    //       course =>
    //         course.tenKhoaHoc
    //           .toLowerCase()
    //           .indexOf(searchKeyword.toLowerCase().trim()) !== -1
    //     );
    //   }

    //   for (let index = courses.length - 1; index >= 0; index--) {
    //     result.push(
    //       <CourseItem
    //         key={index}
    //         course={courses[index]}
    //         deleteCourse={this.props.deleteCourse}
    //         showEditCourse={this.props.showEditCourse}
    //       />
    //     );
    //   }
    // }
    // return result;
    if(courses.length)
    return courses.map((item, index) => {
      return (
        <CourseItem
          key={index}
          course={item}
          deleteCourse={this.props.deleteCourse}
          showEditCourse={this.props.showEditCourse}
        />
      )
    })
  };
}

const mapStateToProps = state => ({
  searchKeyword: state.display.searchKeyword,
  course: state.Course.course
});

const mapDispatchToProps = dispatch => {
  return {
    handleSearch: keyword => {
      dispatch({
        type: SEARCH_COURSES,
        payload: keyword
      });
    },

    deleteCourse: maKhoaHoc => {
      dispatch({
        type: DELETE_COURSE,
        payload: maKhoaHoc
      });
    },

    addCourse: course => {
      dispatch({
        type: ADD_COURSE,
        payload: course
      });
    },

    editCourse: course => {
      dispatch({
        type: "EDIT_COURSE",
        payload: course
      });
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Course);
