import React, { Component } from "react";

import OwlCarousel from "react-owl-carousel";
import "../OwlCarousel2-2.3.4/dist/assets/owl.carousel.css";
import "../OwlCarousel2-2.3.4/dist/assets/owl.theme.default.css";
import Cart from "./Cart";
import CourseService from "../Services/courseService";
import { FETCH_COURSES } from "../Store/Action/type";
import reduxAction from "../Store/Action/action";
import { connect } from "react-redux";

const courseService = new CourseService();

class MyCourse extends Component {

    componentDidMount() {
        courseService
            .layDanhSachKhoaHoc()
            .then(res => {
                this.props.dispatch(reduxAction(FETCH_COURSES, res.data));
            })
            .catch(err => {
                console.log(err);
            });
    }
    render() {
        console.log(this.props.course);
        return (
            <section className="Mycourses">
                <div className="course_content container">
                    <div className="row">
                        <div className="col-md-4 course__item">
                            <div className="course_intro">
                                <h3>The world’s largest selection of courses</h3>
                                <p>
                                    Choose from over 100,000 online video courses with new
                                    additions published every month
                </p>
                            </div>
                        </div>
                        <div className="col-md-8">
                            <div className="Mytab">
                                {/* Nav tabs */}
                                <ul className="nav nav-tabs">
                                    <li className="nav-item">
                                        <a
                                            className="nav-link active"
                                            data-toggle="tab"
                                            href="#dev"
                                        >
                                            Development
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a className="nav-link" data-toggle="tab" href="#des">
                                            Design
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a className="nav-link" data-toggle="tab" href="#business">
                                            Business
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a className="nav-link" data-toggle="tab" href="#it">
                                            IT
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a className="nav-link" data-toggle="tab" href="#personal">
                                            Personal
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a className="nav-link" data-toggle="tab" href="#maketing">
                                            Marketing
                    </a>
                                    </li>
                                    <li className="nav-item">
                                        <a
                                            className="nav-link"
                                            data-toggle="tab"
                                            href="#photography"
                                        >
                                            Photography
                    </a>
                                    </li>
                                </ul>
                                {/* Tab panes */}
                                <div className="tab-content">
                                    <div className="tab-pane container active" id="dev">
                                        <div className="course__items">
                                            <OwlCarousel items={3} nav loop margin={20}>
                                                <Cart item={item} key={index} />
                                            </OwlCarousel>
                                        </div>
                                        </div>
                                        {/* <div className="tab-pane container fade " id="des ">...</div>
                                <div className="tab-pane container fade " id="business ">...</div>
                                <div className="tab-pane container fade " id="it ">...</div>
                                <div className="tab-pane container fade " id="personal ">...</div>
                                <div className="tab-pane container fade " id="maketing ">...</div>
                                <div className="tab-pane container fade " id="photography ">...</div> */}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
            </section>
                );
            }
        }
const StateTopProps = state => {
    return {
                    course: state.course
            };
        };
        
        export default connect(StateTopProps, null)(MyCourse);
