import React, { Component } from "react";
import CourseService from "../../Services/courseService";
import { ADD_COURSE } from "../../Store/Action/type";
import { connect } from "react-redux";
import reduxAction from "../../Store/Action/action";
import { UUID } from "./UUID";
let courseService = new CourseService();
let ngayTao = new Date();
let user = JSON.parse(localStorage.getItem("userLogin"));
class CourseModal extends Component {
  constructor(props) {
    super(props);

    this.state = {
      course: {
        tenKhoaHoc: "",
        biDanh: "agualar",
        moTa: "",
        hinhAnh: "",
        maKhoaHoc: UUID(),
        ngayTao: "",
        maNhom: "GP01",
        luotXem: 155,
        danhGia: 0,
        taiKhoanNguoiTao: "",
        maDanhMucKhoaHoc: "development"
      },
      search: ""
    };
  }

  onChangeSearch = e => {
    this.setState(
      {
        search: e.target.value
      },
      this.props.handleSearch(this.state.search)
    );
  };

  onChange = e => {
    this.setState({
      course: {
        ...this.state.course,
        [e.target.name]: e.target.value
      }
    }, console.log(this.state.course));
  };

  onAddCourse = () => {
    console.log(this.state.course);

    courseService
      .addCourse({
        ...this.state.course
      })
      .then(res => {
        console.log("them thanh cong");
        this.props.dispatch(reduxAction(ADD_COURSE, res.data));
      })
      .catch(err => {
        console.log("lỗi");
      });
  };
  render() {


    return (
      <div>
        <div className="row post_title">
          <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 add__search">
            <button
              className="add__course"
              data-toggle="modal"
              data-target="#modelId"
            >
              <i className="fa fa-plus mr-1"></i> ADD COURSE
            </button>
            <input
              onChange={this.onChangeSearch}
              type="text"
              name="search"
              placeholder="Search Course"
              value={this.state.search}
            />
          </div>
        </div>

        <div
          className="modal fade"
          id="modelId"
          tabIndex={-1}
          role="dialog"
          aria-labelledby="modelTitleId"
          aria-hidden="true"
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={{ fontWeight: "bold" }}>
                  Add New Course
                </h5>
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div className="form-group">
                  <label>Course's Name</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="tenKhoaHoc"
                    value={this.state.tenKhoaHoc}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>ma khoa hoc</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="maKhoaHoc"
                    value={this.state.maKhoaHoc}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>Đánh giá</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="danhGia"
                    value={this.state.danhGia}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>Lượt xem</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="luotXem"
                    value={this.state.luotXem}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>Ngày tạo</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="ngayTao"
                    value={this.state.ngayTao}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>mô tả</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="moTa"
                    onChange={this.onChange}
                    value={this.state.moTa}
                  />
                </div>

                <div className="form-group">
                  <label>Image</label>
                  <input
                    onChange={this.onChange}
                    value={this.state.hinhAnh}
                    style={{ width: "100%" }}
                    name="hinhAnh"
                    type="file"
                    className="form-control"
                  />
                </div>

                <div className="form-group">
                  <label>Người tạo</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="taiKhoanNguoiTao"
                    value={this.state.taiKhoanNguoiTao}
                    onChange={this.onChange}
                  />
                </div>

                <div className="form-group">
                  <label>Danh mục khóa học</label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="maDanhMucKhoaHoc"
                    value={this.state.maDanhMucKhoaHoc}
                    onChange={this.onChange}
                  />
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={this.onAddCourse}
                >
                  Add
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default connect()(CourseModal);
