import React, { Component } from "react";
import { Link } from "react-router-dom";
import Search from "../Search/Search";
import { connect } from "react-redux";
import _ from 'lodash';
import {notify} from '../'
class Header extends Component {
  checkShowAccout = (currentUser) => {
   let userLogin =  localStorage.getItem("userLogin")
   
    if (_.isEmpty(currentUser) && !userLogin)  {
      return (
        <div className="header-user">
          <Link to="/signup" className="btn--white ">
            Login{" "}
          </Link>
          <Link to="login" className="btn--red">
            Sign up
        </Link>


        </div>
      )

    }
    else {
      return (
        <div className="customer-menu menu-account">
          <div class=" toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="true" role="button" tabindex="0">
            <div className="customer-name ">
              <img src="/img/avatar.jpg" alt="avatar" />
              <span>Nguyễn Toàn</span>
              <div className="fa fa-caret-down">

              </div>
            </div>
          </div>
          <ul className="dropdown dropdown-menu">
            <li className="drop-item">
              <Link to="/user">Thông Tin Tài Khoản</Link>
            </li>
            <li className="drop-item">
              <a href="">Danh Sách Yêu Thích</a>
            </li>
            <li className="drop-item">
              <a href="" onClick={this.handleLogOut} >Đăng xuất</a>
            </li>

          </ul>
        </div>
      )
    }
  }
  handleLogOut = () => {
    this.props.setCurrentUser({})
    localStorage.removeItem("userLogin");
    notify
    
  }

  render() {
    let { cart } = this.props;
    return (
      <header className="header ">
        <div className="header__content container ">
          <div className="row">
            <div className="col-md-6 d-flex">
              <Link
                to="/"
                style={{ textDecoration: "none" }}
                className="header-image"
              >
                <img
                  className="img-fluid mr-2"
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjerlRIrbpIb5fWowESAjTpxE8NUAQcJ8ScyunzGOvCvgTjtgwpg&s"
                  width="50%"
                  alt=""
                />{" "}
                CYBERIT
                </Link>

              <Search history={this.props.history} />
            </div>
            <div className="col-md-6  d-flex">
              <div className="header-card">
                <Link to="/cart" style={{ textDecoration: "none" }}>
                  <div className="alert-cart">
                    <i className="fa fa-shopping-bag" />



                    <span>{cart.length}</span>
                  </div></Link>

                <div>
                  <i className="fa fa-heart" aria-hidden="true" />
                </div>

                <div className="alert-cart">
                  {" "}
                  <i className="fa fa-bell" />

                  <span>5</span>
                </div>
              </div>

              {this.checkShowAccout(this.props.currentUser)


              }



            </div>
          </div>
        </div>
      </header>
    );
  }
}


const mapStateToProps = state => {
  return {
    cart: state.cart,
    currentUser: state.currentUser
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    setCurrentUser: user => {
      dispatch({
        type: "SET_CURRENT_USER", user
      });
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
