import { resConnector } from "../Services/index";


const localUser = JSON.parse(localStorage.getItem('userLogin')) ? JSON.parse(localStorage.getItem('userLogin')) : {};
class UserService {
  UserSignUp(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangKy",
      method: "POST",
      data: user
    });
  }


  UserLogin(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangNhap",
      method: "POST",
      data: user
    });
  }

  personalInfo(taiKhoan) {
    return resConnector({
      method: "POST",
      url: "/api/QuanLyNguoiDung/ThongTinTaiKhoan",
      data: {taiKhoan},
    }).then(res => {
     

     

