import { ADD_COURSE_FAVORIES, DELETE_COURSE_FAVORIES } from '../Action/type'
const initialState = []

const courseFavories = (state = initialState, { type, payload }) => {
    switch (type) {

        case ADD_COURSE_FAVORIES: {
            let cartUpdate = [...state];
            let index = cartUpdate.findIndex(sp => sp.maKhoaHoc === payload.maKhoaHoc);

            if (index === -1) {
                cartUpdate = [...cartUpdate, payload]
            } else {
                return cartUpdate
            }
            localStorage.setItem("courseFavorites", JSON.stringify(cartUpdate));
            return cartUpdate;
        }

        case DELETE_COURSE_FAVORIES: {

        }


        default:
            return state
    }
}


export default courseFavories
