import { ADD_TO_CART } from "../Action/type";
const initialState = [];

const Cart = (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_TO_CART: {

      let gioHangCapNhat = [...state];
      let index = gioHangCapNhat.findIndex(sp => sp.maKhoaHoc === payload.maKhoaHoc);

      if (index === -1) {
        gioHangCapNhat = [...gioHangCapNhat, payload]
      }
     localStorage.setItem



      return [...state]
    }

    default:
      return state;
  }
};

export default Cart;
