const initialState = []

const courseFavories = (state = initialState, { type, payload }) => {
    switch (type) {

        case ADD_COURSE_FAVORIES: {
            let stateCapNhat = { ...state }
            stateCapNhat.push(payload)
            return { ...state }
        }

        case DELETE_COURSE_FAVORIES: {

        }


        default:
            return state
    }
}


export default courseFavories
