import React, { Component } from "react";
import UserService from "../../Services/userService";

let userService = new UserService();
export default class Edit extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hoTen: "",
      taiKhoan: "",
      matKhau: "",
      email: "",
      soDT: "",
      maLoaiNguoiDung: "HV",
      maNhom: ""
    };
  }

  onChange = e => {
    this.setState(
      {
       ...sta
      },
     
    );
  };

  onSubmit = e => {
    e.preventDefault();
    userService
      .fixPersonalUser(this.state)
      .then(res => {
        console.log(res.data);
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    let { userProfile } = this.props;
    return (
      <div className="edit__profile">
        <h1>Edit Profile</h1>
        <form onSubmit={this.onSubmit}>
          <div className="fullname">
            <div className="row">
              <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                <label>Tài Khoản :</label>
                <input
                  type="text"
                  placeholder={userProfile.taiKhoan}
                  disabled
                />
              </div>

              <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                <label>Mật khẩu:</label>
                <input type="text" placeholder={userProfile.matKhau} disabled />
              </div>
            </div>
          </div>
          <div className="position">
            <label>Họ Tên:</label>
            <input
              name="hoTen"
              type="text"
              placeholder="Họ Tên"
              onClick={this.onChange}
            />
          </div>
          <div className="bio">
            <label>Email:</label>
            <input
              name="email"
              type="email"
              placeholder="Email"
              onClick={this.onChange}
            />
          </div>
          <div className="social__html_form">
            <h2>Socials</h2>
            <p>
              Add your social profiles links, they will be shown on your public
              profile.
            </p>
            <div className="social__wrapper">
              <div className="row">
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>FACEBOOK</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>TWITTER</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>INSTAGRAM</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>LINKEDIN</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <button className="change__profile" type="submit">
            Change Profile
          </button>
        </form>
      </div>
    );
  }
}
