import { FETCH_COURSES, FETCH_COURSES_CATALOG, FETCH_COURSE_DETAIL } from '../Action/type'
const initialState = {
    course: [],
    courseCatalog: [],
    courseDetail: {}
}




const Course = (state = initialState, { type, payload }) => {


    switch (type) {
        case FETCH_COURSES: {

            state = payload
            return [...state]

        }

        case FETCH_COURSES_CATALOG: {

            state = payload
            return [...state]

        }

        case FETCH_COURSE_DETAIL: {

            state = payload
            return [...state]

        }


        default:
            return state
    }
}
export default Course
