import React, { Component } from "react";
import reduxAction from "../../Store/Action/action";
import { SEARCH_COURSES } from "../../Store/Action/type";
import SearchItem from "./SearchItem";
import { connect } from "react-redux";
class Search extends Component {
    constructor(props) {
        super(props);

        this.state = {
            keyword: ""
        };
    }

    showSearchIem = (courses,searchKeyword) => {

    }

    onChange = e => {
        this.setState(
            {
                keyword: e.target.value
            }, () => {
                this.props.handleSearch(this.state.keyword)
            }

        );
    };
    onSubmit = (e) => {
        e.preventDefault();
        this.props.handleSearch(this.state.keyword)
    }

    render() {
        return (
            <div className="header-control search">
                <input
                    onChange={this.onChange}
                    value={this.state.keyword}
                    className="input-search"
                    type="text"
                    placeholder="Search for anything"
                />
                <div className="search__result">
                    <div className="search__form">
                        <SearchItem />
                    </div>
                </div>
                <button className="btn-search">
                    <i className="fa fa-search"></i>
                </button>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    course: state.Course.courses,
    display: state.Course.display
});

const mapDispatchToProps = dispatch => {
    return {
        handleSearch: keyword => {
            dispatch({
                type: SEARCH_COURSES,
                payload: keyword
            });
        }
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Search);
