import React, { Component } from "react";
import UserItem from "./UserItem";
import UserService from "../../Services/userService";
import { connect } from "react-redux";
import reduxAction from '../../Store/Action/action'
import { ADD_USER } from "../../Store/Action/type";
import {notify} from '../notify/Notify'
let userService = new UserService()
class User extends Component {
  constructor(props) {
    super(props)

    this.state = {
      user: {
        taiKhoan: "",
        matKhau: "",
        hoTen: "",
        soDT: "",
        maLoaiNguoiDung: "HV",
        maNhom: "GP01",
        email: ""
      }
    }
  }




  onChange = (e) => {
    this.setState({
      user: {
        ...this.state.user,
        [e.target.name]: e.target.value
      }
    }, console.log(this.state.user)
    )
  }


  onChangeSearch = e => {
    this.setState(
      {
        search: e.target.value
      },
      this.props.handleSearch(this.state.search)
    );
  };

  onAddUser = () => {
    userService.addUser(this.state.user).then(res => {
      notify("", "Thêm thành công")
      this.props.dispatch(reduxAction(ADD_USER, res.data))


    }).catch(err => {
      console.log(err);
      notify("", "Thêm thất bại")

    })
  }

  render() {
    let { userList } = this.props
    console.log(userList);

    return (
      <div>
        <div className="title">
          <i className="fa fa-user"></i>
          My User
        </div>
        <div className="row users__manager">
          <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 add__user">
            <button
              className="add__course"
              data-toggle="modal"
              data-target="#modelId"
            >
              <i className="fa fa-plus mr-1"></i> ADD User
            </button>
            <input type="text" name="search" placeholder="Search User" onChange={this.onChangeSearch} />
          </div>
        </div>
        {/* modal */}
        <div
          className="modal fade"
          id="modelId"
          tabIndex={-1}
          role="dialog"
          aria-labelledby="modelTitleId"
          aria-hidden="true"
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={{ fontWeight: "bold" }}>
                  Add New User
                </h5>
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div className="form-group">
                  <label>Tài khoản : </label>
                  <input name="taiKhoan" type="text" style={{ width: "100%" }} onChange={this.onChange} />
                </div>

                <div className="form-group">
                  <label>mật khẩu : </label>
                  <input name="matKhau" type="text" style={{ width: "100%" }} onChange={this.onChange} />
                </div>

                <div className="form-group">
                  <label>Họ Tên : </label>
                  <input name="hoTen" type="text" style={{ width: "100%" }} onChange={this.onChange} />
                </div>

                <div className="form-group">
                  <label>Email : </label>
                  <input name="email" type="text" style={{ width: "100%" }} onChange={this.onChange} />
                </div>

                <div className="form-group">
                  <label>Phone : </label>
                  <input name="soDT" type="text" style={{ width: "100%" }} onChange={this.onChange} />
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-danger" onClick={this.onAddUser}>
                  Add
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
        {/* end modal */}

        <table className="table">
          <thead>
            <tr>
              <th>
                ID <i className="fa fa-sort-alpha-desc" aria-hidden="true" />
              </th>
              <th>Fullname </th>
              <th> Phone </th>
              <th> Email </th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {/* <UserItem /> */}
            {userList.map((item, index) => {
              return <UserItem user={item} key={index} />
            })}

          </tbody>
        </table>
      </div>
    );
  }


}
const mapStateToProps = (state) => ({
  userList: state.user
})



export default connect(mapStateToProps)(User)
