import { FETCH_USER, ADD_USER, SEARCH_USER } from "../Action/type"

const initialState = []

const user = (state = initialState, { type, payload }) => {
    switch (type) {

        case FETCH_USER:
            state = payload
            return [...state]
        case ADD_USER: {
            state.push(payload)
            return [...state]
        }
        case SEARCH_USER : {

        }
        default:
            return state
    }
}

export default user
