import React, { Component } from 'react'
// import '../../OwlCarousel2-2.3.4/dist/assets/owl.carousel.css'
// import '../../OwlCarousel2-2.3.4/dist/assets/owl.theme.default.css'


import Countdown from '../Component/Countdown/Countdown';
import Header from '../Component/Header/Header';
import Banner from '../Component/Banner/Banner';
import Promotion from '../Component/Promotion/Promotion';
import TopSelling from '../Component/Topselling/TopSelling';
import Featured from '../Component/Featured/Featured';
import Intro from '../Component/Intro/Intro';
import Footer from '../Component/Footer/Footer';
// import ScrollUpButton from "react-scroll-up-button";
import CourseService from '../Services/courseService'
import reduxAction from '../Store/Action/action';
import { FETCH_COURSES } from '../Store/Action/type'
import { connect } from 'react-redux';
const courseService = new CourseService();
class HomePage extends Component {
    componentDidMount() {
        courseService.layDanhSachKhoaHoc().then((res) => {
            this.props.dispatch(reduxAction(FETCH_COURSES, res.data))
            console.log(res.data);

        }).catch((err) => {
            console.log(err);

        })
    }
    render() {



        return (
            <div className="wrapper">

                {/* header  */}
                <Header />

                {/* banner */}
                <Banner />

                {/* promotion */}

                <Promotion />

                {/* top selling */}
                <TopSelling />
                {/* featured */}
                <Featured />
                {/* intro */}
                <Intro />
                <Countdown />
                {/* footer */}
                <Footer />
                {/*ScrollUpButton  */}

                {/* <ScrollUpButton style={{ width: 75 }} ToggledStyle={{ right: 100 }} /> */}


            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        courseList: state.courseList
    }
}
export default connect()(HomePage)
