import { ADD_TO_CART } from "../Action/type";
const initialState = {};

const Cart = (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_TO_CART: {
      state.push(payload)
      return { ...state }
    }

    default:
      return state;
  }
};

export default Cart;
