import React, { Component } from 'react'

export default class LoaderButton extends Component {
    constructor(props) {
        super(props)

        this.state = {
            loader: flase
        }
    }

    componentDidMount() {
        setTimeout(() => {
            loader: true
        }, 3000);
    }




    render() {
        return (
            <button>
                <div className="spinner-border loader_button" role="status">
                    <span className="sr-only">Loading...</span>
                </div>
            </button>

        )
    }
}
