import { FETCH_USER, ADD_USER } from "../Action/type"

const initialState = []

const user = (state = initialState, { type, payload }) => {
    switch (type) {

        case FETCH_USER:
            state = payload
            return [...state]
        case ADD_USER: {
            state.push(payload)
            return [...state]
        }

        case DELETE_USER: {
            let newArr = [...state.course]
            newArr = state.course.filter(course => course.maKhoaHoc !== action.payload)
            state.course = newArr
            return { ...state }
        }

        default:
            return state
    }
}

export default user
