import { resConnector } from "../Services/index";
class UserService {
  UserSignUp(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangKy",
      method: "POST",
      data: user
    });
  }


  UserLogin(user) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/DangNhap",
      method: "POST",
      data: user
    });
  }

  personalInfo(taiKhoan) {
    return resConnector({
      url: "/api/QuanLyNguoiDung/ThongTinTaiKhoan",
      method: "POST",
      data: taiKhoan
    }).restConnector.default.headers['Authorization'] = 'Bearer' + res.data.accessToken;
  }
}

export default UserService
