import React, { Component } from "react";
import { connect } from "react-redux";

class CourseOfcourses extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showGoCart: false
    };
  }

  onCart = course => {
    this.props.addToCart(course);
    this.setState({
      showGoCart: true
    });
  };

  static getDerivedStateFromProps = (prevProps, nextState) => {
    let cart = JSON.parse(localStorage.getItem("cart"))
      ? JSON.parse(localStorage.getItem("cart"))
      : [];
    let index = cart.findIndex(item => {
      return item.maKhoaHoc === prevProps.course.maKhoaHoc;
    });
    if (index !== -1) {
      nextState.showGoCart = true;
    }
    return null;
  };
  render() {
    let { course } = this.props;
    return (
      <div className="card">
        <div className="card-img">
          <img
            className="img-fluid"
            className="card-img-top"
            src={course.hinhAnh}
            alt=""
          />
        </div>
        <div className="card-body">
          <h4 className="card-title">{course.tenKhoaHoc}</h4>
        </div>
        <div className="button">
          <a href="">$155</a>

          {this.state.showGoCart ? (
            <a href="" className="cart-btn">
              <i className="fa fa-cart"></i>
              Go to Cart
            </a>
          ) : (
            <a
              style={{ color: "red" }}
              href=""
              className="cart-btn"
              onClick={() => this.onCart(course)}
            >
              <i class="fa fa-cart-plus" aria-hidden="true"></i>
            </a>
          )}
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
    return {
      addToCart: course => {
        dispatch({
          type: ADD_TO_CART,
          payload: course
        });
      }
    };
  };

export default connect(null,mapDispatchToProps)(CourseOfcourses);
