import { ADD_COURSE_FAVORIES, DELETE_COURSE_FAVORIES } from '../Action/type'
const initialState = []

const courseFavories = (state = initialState, { type, payload }) => {
    switch (type) {

        case ADD_COURSE_FAVORIES: {
            state.push(payload)
            return [...state]
        }

        case DELETE_COURSE_FAVORIES: {

        }


        default:
            return state
    }
}


export default courseFavories
