import React, { Component } from "react";

export default class FeaturedItem extends Component {
  render() {
    let { courseList,index } = this.props;
    return (
      <div className="featured__list__item">
        <div className="num">{index}</div>
        <div className="course">
          <div className="course-img">
            <img src={courseList.hinhAnh}className="img-fluid" alt="" />
          </div>
          <div className="course-description">
            <p>{courseList.tenKhoaHoc}</p>
            <div className="star-rating">
              <i className="fa fa-star"></i>
              <i className="fa fa-star"></i>
              <i className="fa fa-star"></i>
              <i className="fa fa-star"></i>
            </div>
            <span>499.000 ₫</span>
          </div>
        </div>
      </div>
    );
  }
}
