import { ADD_TO_CART } from "../Action/type";
import { DELETE_CART } from '../Action/type'
let data = JSON.parse(localStorage.getItem("cart"))
const initialState = data ? data : [];

const Cart = (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_TO_CART: {

      let gioHangCapNhat = [...state];
      let index = gioHangCapNhat.findIndex(sp => sp.maKhoaHoc === payload.maKhoaHoc);

      if (index === -1) {
        gioHangCapNhat = [...gioHangCapNhat, payload]
      } else {
        return gioHangCapNhat
      }
      localStorage.setItem("cart", JSON.stringify(gioHangCapNhat));
      return gioHangCapNhat;




    }
    case DELETE_CART: {
      let mangCapNhat = [...state]
      let index = mangCapNhat.findIndex(item => item.maKhoaHoc === payload.maKhoaHoc)
      mangCapNhat.splice(index, 1)
      state = mangCapNhat
      localStorage.setItem("cart", JSON.stringify(mangCapNhat));
      return [...state]
    }

    default:
      return state;
  }
};

export default Cart;
