import React, { Component } from "react";
import CourseService from "../../Services/courseService";
import reduxAction from "./Store/Action/action";
let courseService = new CourseService();
export default class Pagination extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: null,
      total: null,
      per_page: null,
      current_page: null
    };
  }

  componentDidMount() {
    courseService
      .fethchCoursePagination()
      .then(res => {
        this.dispatch.reduxAction(FETCH_COURSE_PAGINATION, res.data);
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <div className="pagi">
        <ul className="pagi__wrapper">
          <li className="pagi__item disabled">
            <i className="fa fa-chevron-left" aria-hidden="true" />
          </li>
          <li className="pagi__item active" id="1">
            1
          </li>
          <li className="pagi__item " id="2">
            2
          </li>
          <li className="pagi__item " id="3">
            3
          </li>
          <li className="pagi__item disabled">...</li>
          <li className="pagi__item ">5</li>
          <li className="pagi__item ">
            <i className="fa fa-chevron-right" aria-hidden="true" />
          </li>
        </ul>
      </div>
    );
  }
}
