  render() {
    let { courseid } = this.props.match.param
    let { course } = this.props;


    return (
      <div>
        <Header />
        <CourseItem />
        <Viewing />

        <Footer />
      </div>
    );
  }

  showItem = (course, id) => {
    let res = null;
    for (let item of course) {
      if (item.maKhoaHoc === id) {
       return <CourseItem/> 
      }
    }

    return res;



  }
}
const mapStateToProps = state => ({
  // courseDetail: state.Course.courseDetail || {
