  //   tenKhoaHoc: "",
  //   hinhAnh: "",
  //   nguoiTao: {
  //     taiKhoan: "",
  //     hoTen: ""
  //   }
  // },

  course: state.Course.course
});


const mapDispatchToProps = (dispatch, props) => {
  return {
    handleAddCart: course => {
      dispatch(addToCart(course));
    }
  }
}




export default connect(mapStateToProps, mapDispatchToProps)(CourseDetailPage);
