import React, { Component } from "react";
import { Link } from "react-router-dom";
import Search from "../Search/Search";
import { connect } from "react-redux";

class Header extends Component {
  render() {
    let { cart } = this.props;
    return (
      <header className="header ">
        <div className="header__content container ">
          <div className="row">
            <div className="col-md-6 d-flex">
              <Link
                to="/"
                style={{ textDecoration: "none" }}
                className="header-image"
              >
                <img
                  className="img-fluid mr-2"
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjerlRIrbpIb5fWowESAjTpxE8NUAQcJ8ScyunzGOvCvgTjtgwpg&s"
                  width="50%"
                  alt=""
                />{" "}
                CYBERIT
              </Link>

              <Search history={this.props.history} />
            </div>
            <div className="col-md-6  d-flex">
              <div className="header-card">
                <Link to="/cart" style={{ textDecoration: "none" }}>
                  <div className="alert-cart">
                    <i className="fa fa-shopping-bag" />



                    <span>{cart.length}</span>
                  </div></Link>

                <div>
                  <i className="fa fa-heart" aria-hidden="true" />
                </div>

                <div className="alert-cart">
                  {" "}
                  <i className="fa fa-bell" />

                  <span>5</span>
                </div>
              </div>
              <div className="header-user">
                <Link to="/signup" className="btn--white ">
                  Login{" "}
                </Link>
                <Link to="login" className="btn--red">
                  Sign up
                </Link>

                <img src="" alt=""/>
                <h1>Hi Toan</h1>
                <button className="btn btn-info">Log Out</button>
              </div>

            </div>
          </div>
        </div>
      </header>
    );
  }
}
const mapStateToProps = state => {
  return {
    cart: state.cart,
    currentUser: state.currentUser
  };
};

export default connect(mapStateToProps, null)(Header);
