import React, { Component } from "react";
import { connect } from "react-redux";
import { ADD_TO_CART } from "../../Store/Action/type";

class CourseOfcourses extends Component {
 

  onCart = course => {
    this.props.addToCart(course);
    this.setState({
      showGoCart: true
    });
  };

  
  render() {
    let { course } = this.props;
    return (
      <div className="card">
        <div className="card-img">
          <img
            className="img-fluid"
            className="card-img-top"
            src={course.hinhAnh}
            alt=""
          />
        </div>
        <div className="card-body">
          <h4 className="card-title">{course.tenKhoaHoc}</h4>
        </div>
        <div className="button">
          <a href="">$155</a>

          {this.state.showGoCart ? (
            <a href="" className="cart-btn">
              <i className="fa fa-cart"></i>
              Go to Cart
            </a>
          ) : (
            <a
              style={{ color: "red" }}
              href=""
              className="cart-btn"
              onClick={() => this.onCart(course)}
            >
              <i class="fa fa-cart-plus" aria-hidden="true"></i>
            </a>
          )}
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
    return {
      addToCart: course => {
        dispatch({
          type: ADD_TO_CART,
          payload: course
        });
      }
    };
  };

export default connect(null,mapDispatchToProps)(CourseOfcourses);
