// import Banner from "../../Layout/Banner";
// import TopCategories from "../../Layout/TopCategories";
// import Testimonials from "../../Layout/Testimonials";
// import Footer from "../../Layout/Footer";
import React, { Component } from 'react'
import CourseService from "../../Services/courseService";
import { FETCH_COURSES } from "../../Store/Action/type";
import reduxAction from "../../Store/Action/action";
import { connect } from "react-redux";
const courseService = new CourseService();


class Home extends Component {
    componentDidMount() {
        courseService
            .layDanhSachKhoaHoc()
            .then(res => {
                this.props.dispatch(reduxAction(FETCH_COURSES, res.data));
            })
            .catch(err => {
                console.log(err);
            });
    }
    render() {
        let { course } = this.props
        return (
            <div>
                <Header />
                <MyCover />
                <MyIntro />
                <MyCourse course={course} />
                <MyStudent course={course} />
                <Banner />
                <TopCategories />
                <Testimonials />
                <Footer />
            </div>
        )
    }
}
const StateTopProps = state => {
    return {
        course: state.course
    };
};
export default connect(StateTopProps)(Home)
