import React, { Component } from "react";
import UserService from "../../Services/userService";
import { connect } from "react-redux";

let userService = new UserService();
class Edit extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: this.props.userProfile
      
    };
  }
  onChange = (e) => {
    this.setState({
      [e.target.user.name]: e.target.value

    }
    )

  }




  onSubmit = e => {
    e.preventDefault();
    userService
      .fixPersonalUser(this.state.user)
      .then(res => {
        this.setState()
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    console.log(this.state.user.hoTen);

    let { userProfile } = this.props;
    return (
      <div className="edit__profile">
        <h1>Edit Profile</h1>
        <form onSubmit={this.onSubmit}>
          <div className="fullname">
            <div className="row">
              <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                <label>Tài Khoản :</label>
                <input
                  name="taiKhoan"
                  type="text"
                  placeholder={userProfile.taiKhoan}
                  onChange={this.onChange}
                />
              </div>

              <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                <label>Mật khẩu:</label>
                <input type="text" placeholder={userProfile.matKhau} name="matKhau" onChange={this.onChange} />
              </div>
            </div>
          </div>
          <div className="position">
            <label>Họ Tên:</label>
            <input
              name="hoTen"
              type="text"
              placeholder="Họ Tên"
              onChange={this.onChange}
            />
          </div>
          <div className="bio">
            <label>Email:</label>
            <input
              name="email"
              type="email"
              placeholder="Email"
              onChange={this.onChange}
            />
          </div>
          <div className="social__html_form">
            <h2>Socials</h2>
            <p>
              Add your social profiles links, they will be shown on your public
              profile.
            </p>
            <div className="social__wrapper">
              <div className="row">
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>FACEBOOK</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>TWITTER</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>INSTAGRAM</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
                <div className="col-sm-6 col-md-6 col-lg-6 col-xl-6">
                  <div className="socila-item">
                    <div>LINKEDIN</div>
                    <input type="text" placeholder="Enter Your FaceBook URl" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <button className="change__profile" type="submit">
            Change Profile
          </button>
        </form>
      </div>
    );
  }
}


const mapStateToProps = state => ({
  editUser: state.editUser
});


const mapDispatchToProps = dispatch => {
  return {
    editUser: user => {
      dispatch({
        type: "EDIT_USER",
        payload: user
      });
    }
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Edit)
